class ToDoTasks extends React.Component {

    state = {
        tasksList: this.props.tasks,
    }

    removeTask = (e) => {
       e.preventDefault();
       this.props.handleTask(e.target.value,tasks,deleted)
    }

    completeTask = (e) => {
        e.preventDefault();
        this.props.handleTask('complete',e.target.value)
    }

    render() {
        return (
            <div>
                {this.props.tasks.map((t) => {
                    return (
                    <>
                        <h1>{t}</h1>
                        <button value={t} onClick={this.removeTask} class='btn btn-danger' >Delete</button>
                        <button value={t} onClick={this.completeTask} class='btn btn-info' >Complete</button>
                      </>  
                    )
                })}
            </div>
        );

    }


}





export default ToDoTasks;
