import React, { useState } from 'react';

class ToDoForm extends React.Component {

    state = {
        task: ''
    }


    handleChange = (e) => {
        this.setState({ [e.target.name]: e.target.value })
    }

    handleSubmit = (e) => {
        e.preventDefault();
        // console.log(e.target.task.value)
        this.props.addTask(this.state.task)
        this.setState({ task: '' })
    }

    render() {
        return (
            // <form onSubmit={this.handleSubmit}>
            //     <input type="text" className='form-control' aria-describedby="button-addon2" name='task' onChange={this.handleChange} value={this.state.task} />
            //     <button type="submit" className='btn btn-outline-secondary' id="button-addon2">Add Task</button>
            // </form>
            <div class="input-group mb-3">
                <input type="text" class="form-control" name='task' onChange={this.handleChange} value={this.state.task} aria-label="Recipient's username" aria-describedby="button-addon2" />
                    <div class="input-group-append">
                        <button class="btn btn-success" type="button" id="button-addon2" onClick={this.handleSubmit}>Button</button>
                    </div>
          </div>
                );

                }


        }





export default ToDoForm;
