import React, { useState } from 'react';
import ToDoForm from './ToDoForm';
import ToDoTasks from './ToDoTasks';
// import ToDoDone from './ToDoDone';
// import ToDoDeleted from './ToDoDeleted';

class ToDo extends React.Component {

    state = {
        tasks: [],
        completed: [],
        deleted: []
    }

    addTask = (task) => {
        this.setState({ tasks: this.state.tasks.concat(task) });
    }

    removeTask = (task) => {
        let tasks = [...this.state.tasks];
        let index = tasks.indexOf(task);
        if (index !== -1) {
        tasks.splice(index,1);
        this.setState({tasks: tasks});
        }
        this.setState({ deleted: this.state.deleted.concat(task) });
    }

    completeTask = (task) => {
        let tasks = [...this.state.tasks];
        let index = tasks.indexOf(task);
        if (index !== -1) {
        tasks.splice(index,1);
        this.setState({tasks: tasks});
        }
        this.setState({ completed: this.state.completed.concat(task) });
    }

    componentDidUpdate(){
        console.log('after',this.state.deleted[0])
    }
    render() {
        return (
            <>
                <ToDoForm addTask={this.addTask} />
                <ToDoTasks tasks={this.state.tasks} removeTask={this.removeTask} />
                {/* <ToDoDone />
                <ToDoDeleted /> */}
            </>
        );


    }
}





export default ToDo;
