import React, { useState } from 'react';
import ToDoForm from './ToDoForm';
import ToDoTasks from './ToDoTasks';
// import ToDoDone from './ToDoDone';
// import ToDoDeleted from './ToDoDeleted';

class ToDo extends React.Component {

    state = {
        tasks: [],
        completed: [],
        deleted: []
    }

    addTask = (task) => {
        this.setState({ tasks: this.state.tasks.concat(task) });
    }

    removeTask = (task) => {
        let index = this.state.tasks.indexOf(task);
        console.log('deleting',index);
        this.setState({ tasks: this.state.tasks.splice(index,1) });
        console.log(this.state.tasks);
        this.setState({ deleted: this.state.deleted.concat(task) });
        console.log(this.state.deleted);
    }
    render() {
        return (
            <>
                <ToDoForm addTask={this.addTask} />
                <ToDoTasks tasks={this.state.tasks} />
                {/* <ToDoDone />
                <ToDoDeleted /> */}
            </>
        );


    }
}





export default ToDo;
