import React from 'react';

class ToDoTasks extends React.Component {

    state = {
        tasksList: this.props.tasks,
    }

    removeTask(e){
       e.preventDefault();

    }

    render() {
        return (
            <div>
                {this.props.tasks.map((t) => {
                    return (
                    <>
                        <h1>{t}</h1>
                        <button onClick='removeTask'>x</button>
                      </>  
                    )
                })}
            </div>
        );

    }


}





export default ToDoTasks;
